INSERT INTO `song` VALUES ('389', '52', '澪恩Seiwen-雅俗共赏', '', '2021-12-09 09:29:30', '2021-12-09 09:29:30', '/img/songPic/dengziqi.jpg', '是否每一部戏都看得完整场\r\n\r\n是否每一天过得都有多难忘\r\n\r\n表情迟钝可能因为比较爱想\r\n\r\n不擅长眉目表达\r\n\r\n总在盼望 总在失望\r\n\r\n日子还不都这样\r\n\r\n俗的无畏 雅的轻狂\r\n\r\n还不都是一副臭皮囊\r\n\r\n他们说快写一首情歌雅俗共赏\r\n\r\n落笔传神还要容易传唱\r\n\r\n上得厅堂也下得厨房\r\n\r\n就像我一直在找的姑娘\r\n\r\n快写一首情歌雅俗共赏\r\n\r\n打完字谜还要接着打榜\r\n\r\n如果胡同弄堂全都播放\r\n\r\n气韵里居然添了些孤芳自赏\r\n\r\n是否每一场美梦醒来都很爽\r\n\r\n是否每一次成熟都徒增了业障\r\n\r\n比痛和痒更多的\r\n\r\n是不痛不痒\r\n\r\n所以我爱进剧场\r\n\r\n总在盼望 总在失望\r\n\r\n日子还不都这样\r\n\r\n俗的无畏 雅的轻狂\r\n\r\n还不都是一副臭皮囊\r\n\r\n他们说快写一首情歌雅俗共赏\r\n\r\n落笔传神还要容易传唱\r\n\r\n上得厅堂也下得厨房\r\n\r\n就像我一直在找的姑娘\r\n\r\n快写一首情歌雅俗共赏\r\n\r\n打完字谜还要接着打榜\r\n\r\n如果胡同弄堂全都播放\r\n\r\n气韵里居然添了些孤芳自赏\r\n\r\n谁的故事有营养\r\n\r\n大俗或大雅的都在理直气壮\r\n\r\n洒狗血或白雪的现场\r\n\r\n都邀我观赏\r\n\r\n还真是大方\r\n\r\n快写一首情歌雅俗共赏\r\n\r\n落笔传神还要容易传唱\r\n\r\n上得厅堂也下得厨房\r\n\r\n就像我一直在找的姑娘\r\n\r\n有没有一种生活雅俗共赏\r\n\r\n情节起伏跌宕让人向往\r\n\r\n满纸荒唐中窥见满脸沧桑\r\n\r\n触到神经就要懂得鼓掌\r\n\r\n别说一不在乎二没期望\r\n\r\n太超脱 中枪中奖感觉会一样', '/song/1639013370277澪恩Seiwen - 雅俗共赏.mp3');
